"use strict";

var oName = document.getElementById("uname");
var oPas = document.getElementById("upas");
var oPhone = document.getElementById("uphone");
var oSub = document.querySelector(".chjian");
var oGo = document.querySelector(".log-L");

oGo.onclick = function () {
  var uname = oName.value;
  var upas = oPas.value;

  if (!uname) {
    // alert("用户名不能为空！")
    return;
  } // 获取cookie：


  var cookieStr = getCookie("register") ? getCookie("register") : ''; // 转为对象：

  var cookieObj = convertStrToObj(cookieStr);

  if (uname in cookieObj) {
    if (cookieObj[uname] === upas) {
      alert("登录成功");
      location.href = "../index.html";
      oName.value = "";
      oPas.value = "";
    } else {
      alert("请确认密码是否正确！");
    }
  } else {
    alert("用户名不存在！");
  }
};